package com.Adept.Java;

public class Conversions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// One Datatype to another datatype 

		
		String s = "1000";
		System.out.println("The value of string s " +s);
		int i = Integer.parseInt(s);
		System.out.println("The value of i after conversion "+i);
		float f = Float.parseFloat(s);
		System.out.println("The value in float after conversion "+f);
		
	}

}
